					if(strcmp(_tabuleiroURSS[(i - 1) * ORDEM_MATRIZ_TABULEIRO + j - 1].conteudo, ASCII_CARACTERE_SIMBOLO_DA_AGUA_STRING) == 0)
						printf("*  |");
