			imprimirCadeiaCaractere('~', 5, ASCII_COR_PADRAO, SEM_QUEBRA_DE_LINHA);
			imprimirCadeiaCaractere('-', 4 * (ORDEM_MATRIZ_TABULEIRO + 1), ASCII_COR_PADRAO, SEM_QUEBRA_DE_LINHA);
