void limparStdin(void){
    int c;
    while ((c = getchar()) != '\n' && c != EOF){}
}

void limparTela(void){
	printf("\e[H\e[2J");
}

void teclaContinuar(void){
  struct termios velhoT, novoT;
  printf("Pressione qualquer tecla para continuar.\n");
  tcgetattr(STDIN_FILENO, &velhoT);
  novoT = velhoT;
  novoT.c_lflag &= ~(ICANON | ECHO);
  tcsetattr(STDIN_FILENO, TCSANOW, &novoT);
  getchar();
  tcsetattr (STDIN_FILENO, TCSANOW, &velhoT);
}
